<!-- Location --> |subdivision_type = [[List of countries|Country]] |subdivision_name = [[United States]] |subdivision_type1 = [[Political divisions of the United States|State]] |subdivision_name1 = [[Wisconsin]] |subdivision_type2 = [[List of counties in Wisconsin|County]] |subdivision_name2 = [[Milwaukee County, Wisconsin|Milwaukee]]
