ves/forms/exhibits/SD%20Towns.pdf}}</ref> |established_title2 = <!-- Incorporated (town) --> |established_date2 = |established_title3 = <!-- Incorporated (city) --> |established_date3 = <!-- Area ---------------------> |area_magnitude = |unit_pref = Imperial |area_footnotes = <ref name ="Gazetteer files"/> |area_total_km2 = 3.47 |area_land_km2 = 3.47 |area_water_km2 = 0 |area_total_sq_mi = 1.34 |area_land_sq_mi = 1.34 |area_water_sq_mi = 0 |area_water_percent = |area_urban_km2 = |area_urban_sq_mi = |area_metro_km2 = |area_metro_sq_mi = |area_blank1_title = |area_blank1_km2 = |area_blank1_sq_mi = <!-- Population -----------------------> |population_as_of = [[2010 United States Census|2010]] |population_est = 954 |pop_est_as_of = 2012<ref name="2012 Pop Estimate">{{cite web|title=Population Estimates|url=http://www.census.gov/popest/data/cities/totals/2012/SUB-EST2012.html|publisher=[[United States Census Bureau]]|accessdate=2013-05-29}}</ref> |population_footnotes = <ref name ="FactFinder"/> |population_note = |population_total = 954 |population_density_km2 = 274.9 |population_density_sq_mi = 711.9 |population_metro = |population_density_metro_km2 = |population_density_metro_sq_mi = |population_urban = |population_density_urban_km2 = |population_density_urban_sq_mi = |population_blank1_title = |population_blank1 = |population_density_blank1_km2 = |population_density_blank1_sq_mi = <!-- General information ---------------> |timezone = [[North American Central Time Zone|Central (CST)]] |utc_offset = -6 |timezone_DST = CDT |utc_offset_DST = -5 |coordinates_display = inline,title |coordinates_type = region:US_type:city |latd = 45 |latm = 26 |lats = 42 |latNS = N |longd = 99 |longm = 1 |longs = 49 |longEW = W |elevation_footnotes = <!--for references: use <ref> </ref> tags--> |elevation_m = 468 |elevation_ft = 1535 <!-- Area/postal codes & others --------> |postal_code_type = [[ZIP code]] |postal_code = 57451 |area_code = [[Area code 605|605]] |blank_name = [[Federal Info